import React, {useState} from "react";
import { fetchMarketPrices, predictMarket } from "../api";

export default function MarketCard(){
  const [crop, setCrop] = useState("wheat");
  const [price, setPrice] = useState(null);
  const [prediction, setPrediction] = useState(null);

  async function getPrice(){
    const r = await fetchMarketPrices(crop);
    setPrice(r);
  }
  async function getPrediction(){
    const r = await predictMarket(crop, 7);
    setPrediction(r);
  }

  return (
    <div className="app_card app-card">
      <h2>Market Prices & Prediction</h2>
      <select value={crop} onChange={e=>setCrop(e.target.value)}>
        <option value="wheat">Wheat</option>
        <option value="rice">Rice</option>
        <option value="onion">Onion</option>
      </select>
      <div style={{marginTop:8}}>
        <button onClick={getPrice}>Get Latest Price</button>
        <button onClick={getPrediction} style={{marginLeft:8}}>Predict (7 days)</button>
      </div>
      <div style={{marginTop:12}}>
        {price && <div>Latest: ₹{price.price_rupee_per_quintal} / quintal (date: {price.date})</div>}
        {prediction && <div>Predicted price (7 days): ₹{prediction.predicted_price}</div>}
      </div>
    </div>
  );
}
