import React, {useState} from "react";
import { chatAI } from "../api";

export default function ChatbotCard(){
  const [query, setQuery] = useState("");
  const [answer, setAnswer] = useState("");
  const [loading, setLoading] = useState(false);

  async function ask(){
    setLoading(true);
    try{
      const res = await chatAI(query);
      setAnswer(res.answer || res);
    }catch(e){
      setAnswer("Failed to fetch AI");
    }
    setLoading(false);
  }

  return (
    <div className="app-card">
      <h2>AI Helpdesk</h2>
      <textarea value={query} onChange={e=>setQuery(e.target.value)} rows={4} placeholder="Ask about soil, pests, loans..."></textarea>
      <div style={{marginTop:8}}>
        <button onClick={ask} disabled={!query || loading}>{loading? "Thinking...":"Ask AI"}</button>
      </div>
      {answer && <div style={{marginTop:12, whiteSpace:'pre-wrap'}} className="app-card">{answer}</div>}
    </div>
  );
}
