import React, {useState, useEffect} from "react";
import { fetchGuides } from "../api";

export default function GuidesCard(){
  const [topic, setTopic] = useState("");
  const [guides, setGuides] = useState([]);

  async function loadGuides(){
    const r = await fetchGuides(topic);
    setGuides(r);
  }

  useEffect(()=>{ loadGuides(); }, []);

  return (
    <div className="app-card">
      <h2>Guides</h2>
      <input placeholder="Filter by topic (e.g., pest control)" value={topic} onChange={e=>setTopic(e.target.value)} />
      <div style={{marginTop:8}}>
        <button onClick={loadGuides}>Search Guides</button>
      </div>
      <div style={{marginTop:12}}>
        {guides.length===0 ? <div>No guides found.</div> : guides.map((g,idx)=>(
          <div key={idx} style={{marginBottom:10}}>
            <b>{g.title}</b>
            <div style={{fontSize:13}}>{g.content}</div>
          </div>
        ))}
      </div>
    </div>
  );
}
