import React, { useState } from "react";
import { chatAI } from "../api";

export default function ChatbotCard() {
  const [query, setQuery] = useState("");
  const [answer, setAnswer] = useState("");
  const [loading, setLoading] = useState(false);
  const [conversationHistory, setConversationHistory] = useState([]);

  async function ask() {
    if (!query.trim()) return;
    
    setLoading(true);
    const userQuery = query;
    
    // Add user message to history
    const newHistory = [...conversationHistory, { type: 'user', content: userQuery }];
    setConversationHistory(newHistory);
    setQuery("");
    
    try {
      const res = await chatAI(userQuery);
      const aiResponse = res.answer || res;
      setAnswer(aiResponse);
      
      // Add AI response to history
      setConversationHistory([...newHistory, { type: 'ai', content: aiResponse }]);
    } catch (e) {
      const errorMsg = "Sorry, I'm having trouble connecting right now. Please try again.";
      setAnswer(errorMsg);
      setConversationHistory([...newHistory, { type: 'ai', content: errorMsg }]);
    }
    
    setLoading(false);
  }

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      ask();
    }
  };

  const suggestedQuestions = [
    "What's the best fertilizer for rice cultivation?",
    "How to prevent common pest diseases?",
    "When is the optimal harvest time?",
    "What are the current market prices?"
  ];

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 max-w-4xl mx-auto">
      {/* Header */}
      <div className="flex items-center gap-3 mb-6">
        <div className="w-10 h-10 bg-green-500 rounded-lg flex items-center justify-center">
          <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 10h.01M12 10h.01M16 10h.01M9 16H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-4l-4 4z" />
          </svg>
        </div>
        <div>
          <h2 className="text-xl font-semibold text-gray-800">AI Assistant</h2>
          <p className="text-sm text-gray-600">Get expert advice on farming, crops, and agriculture</p>
        </div>
      </div>

      {/* Conversation History */}
      {conversationHistory.length > 0 && (
        <div className="mb-6 max-h-64 overflow-y-auto bg-gray-50 rounded-lg p-4 space-y-3">
          {conversationHistory.map((message, index) => (
            <div key={index} className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}>
              <div className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                message.type === 'user' 
                  ? 'bg-green-500 text-white' 
                  : 'bg-white border border-gray-200 text-gray-800'
              }`}>
                <p className="text-sm">{message.content}</p>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Suggested Questions */}
      {conversationHistory.length === 0 && (
        <div className="mb-6">
          <p className="text-sm text-gray-600 mb-3">Try asking about:</p>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
            {suggestedQuestions.map((question, index) => (
              <button
                key={index}
                onClick={() => setQuery(question)}
                className="text-left p-3 bg-gray-50 hover:bg-gray-100 rounded-lg border border-gray-200 transition-colors text-sm text-gray-700"
              >
                {question}
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Input Area */}
      <div className="space-y-4">
        <div className="relative">
          <textarea
            className="w-full p-4 border border-gray-300 rounded-lg resize-none focus:ring-2 focus:ring-green-500 focus:border-transparent outline-none transition-all"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            onKeyPress={handleKeyPress}
            rows={3}
            placeholder="Ask about soil health, pest control, crop management, market prices, loans, or any farming question..."
            disabled={loading}
          />
          <div className="absolute bottom-3 right-3 text-xs text-gray-400">
            Press Enter to send, Shift+Enter for new line
          </div>
        </div>

        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2 text-xs text-gray-500">
            <div className="w-2 h-2 bg-green-400 rounded-full"></div>
            <span>AI Assistant Online</span>
          </div>
          
          <button
            onClick={ask}
            disabled={loading || !query.trim()}
            className="px-6 py-2 bg-green-500 hover:bg-green-600 disabled:bg-gray-300 disabled:cursor-not-allowed text-white rounded-lg font-medium transition-colors flex items-center gap-2"
          >
            {loading ? (
              <>
                <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                Processing...
              </>
            ) : (
              <>
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                </svg>
                Ask AI
              </>
            )}
          </button>
        </div>
      </div>

      {/* Current Answer Display (if you want to keep it separate from history) */}
      {answer && conversationHistory.length === 0 && (
        <div className="mt-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
          <div className="flex items-start gap-3">
            <div className="w-8 h-8 bg-green-500 rounded-full flex items-center justify-center flex-shrink-0 mt-1">
              <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
              </svg>
            </div>
            <div className="flex-1">
              <h4 className="font-medium text-gray-800 mb-2">AI Response:</h4>
              <p className="text-gray-700 leading-relaxed whitespace-pre-wrap">{answer}</p>
            </div>
          </div>
        </div>
      )}

      {/* Footer */}
      <div className="mt-6 pt-4 border-t border-gray-200">
        <p className="text-xs text-gray-500 text-center">
          Powered by AgriSmartAI • Get instant answers to your farming questions
        </p>
      </div>
    </div>
  );
}
