import React, { useState, useCallback } from "react";

// Mock API functions for demonstration
const fetchMarketPrices = async (crop) => {
  // Simulate API delay
  await new Promise(resolve => setTimeout(resolve, 1000));
  
  // Mock data
  const mockPrices = {
    wheat: { price_rupee_per_quintal: 2150, date: "2025-09-01" },
    rice: { price_rupee_per_quintal: 3200, date: "2025-09-01" },
    onion: { price_rupee_per_quintal: 1800, date: "2025-09-01" }
  };
  
  return mockPrices[crop];
};

const predictMarket = async (crop, days) => {
  // Simulate API delay
  await new Promise(resolve => setTimeout(resolve, 1200));
  
  // Mock prediction data
  const mockPredictions = {
    wheat: { predicted_price: 2280, confidence: 0.85 },
    rice: { predicted_price: 3350, confidence: 0.78 },
    onion: { predicted_price: 1650, confidence: 0.72 }
  };
  
  return mockPredictions[crop];
};

// Constants
const CROP_OPTIONS = [
  { value: "wheat", label: "Wheat" },
  { value: "rice", label: "Rice" },
  { value: "onion", label: "Onion" },
];

const PREDICTION_DAYS = 7;

/**
 * MarketCard component for displaying market prices and predictions
 * @returns {JSX.Element} Market card component
 */
export default function MarketCard() {
  // State management
  const [selectedCrop, setSelectedCrop] = useState("wheat");
  const [marketData, setMarketData] = useState({
    price: null,
    prediction: null,
    isLoadingPrice: false,
    isLoadingPrediction: false,
    priceError: null,
    predictionError: null,
  });

  /**
   * Handles fetching current market price
   */
  const handleFetchPrice = useCallback(async () => {
    setMarketData(prev => ({
      ...prev,
      isLoadingPrice: true,
      priceError: null,
    }));

    try {
      const priceData = await fetchMarketPrices(selectedCrop);
      setMarketData(prev => ({
        ...prev,
        price: priceData,
        isLoadingPrice: false,
      }));
    } catch (error) {
      console.error("Failed to fetch market price:", error);
      setMarketData(prev => ({
        ...prev,
        priceError: "Failed to fetch market price. Please try again.",
        isLoadingPrice: false,
      }));
    }
  }, [selectedCrop]);

  /**
   * Handles fetching market prediction
   */
  const handleFetchPrediction = useCallback(async () => {
    setMarketData(prev => ({
      ...prev,
      isLoadingPrediction: true,
      predictionError: null,
    }));

    try {
      const predictionData = await predictMarket(selectedCrop, PREDICTION_DAYS);
      setMarketData(prev => ({
        ...prev,
        prediction: predictionData,
        isLoadingPrediction: false,
      }));
    } catch (error) {
      console.error("Failed to fetch market prediction:", error);
      setMarketData(prev => ({
        ...prev,
        predictionError: "Failed to fetch prediction. Please try again.",
        isLoadingPrediction: false,
      }));
    }
  }, [selectedCrop]);

  /**
   * Handles crop selection change
   * @param {React.ChangeEvent<HTMLSelectElement>} event
   */
  const handleCropChange = (event) => {
    const newCrop = event.target.value;
    setSelectedCrop(newCrop);
    
    // Clear previous data when crop changes
    setMarketData(prev => ({
      ...prev,
      price: null,
      prediction: null,
      priceError: null,
      predictionError: null,
    }));
  };

  /**
   * Formats currency for display
   * @param {number} amount
   * @returns {string} Formatted currency string
   */
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 2,
    }).format(amount);
  };

  /**
   * Formats date for display
   * @param {string} dateString
   * @returns {string} Formatted date string
   */
  const formatDate = (dateString) => {
    try {
      return new Date(dateString).toLocaleDateString('en-IN', {
        year: 'numeric',
        month: 'short',
        day: 'numeric',
      });
    } catch (error) {
      return dateString; // Fallback to original string if parsing fails
    }
  };

  const {
    price,
    prediction,
    isLoadingPrice,
    isLoadingPrediction,
    priceError,
    predictionError,
  } = marketData;

